import { BaseComponentProps } from '../shared.ts';
/**
 * The props type for {@link !components.ProgressBar}.
 *
 * @category Component Props
 */
export interface ProgressBarProps extends BaseComponentProps {
    /**
     * The text that displays above the progress bar.
     *
     */
    title?: string;
    /**
     * The accessibility label.
     *
     */
    'aria-label'?: string;
    /**
     * When set to `true`, the progress bar will display the completion percentage.
     *
     * @defaultValue `false`
     */
    showPercentage?: boolean;
    /**
     * The number representing the progress so far.
     *
     * @defaultValue `0`
     */
    value?: number;
    /**
     * The maximum value of the progress bar.
     *
     * @defaultValue `100`
     */
    maxValue?: number;
    /**
     * The text that explains the current state of the `value` property. For example, `"150 out of 250"`.
     *
     */
    valueDescription?: string;
    /**
     * The color to indicate progress sentiment.
     *
     * @defaultValue `"success"`
     */
    variant?: 'success' | 'danger' | 'warning';
    /** @deprecated use maxValue instead */
    valueMax?: number;
}
