import { ReactNode } from 'react';
import { BaseComponentProps } from '../shared.ts';
/**
 * The props type for {@link !components.Tile}.
 *
 * @category Component Props
 */
export interface TileProps extends BaseComponentProps {
    /**
     * Sets the content that will render inside the component. This prop is passed implicitly by providing sub-components.
     */
    children: ReactNode;
    /**
     * When set to `true`, reduces the amount of padding in the tile.
     *
     * @defaultValue `false`
     */
    compact?: boolean;
    /**
     * When set to `true`, removes left and right padding from the tile contents.
     *
     * @defaultValue `false`
     */
    flush?: boolean;
}
