import { ReactNode } from 'react';
import { BaseComponentProps } from '../shared.ts';
/**
 * The props type for {@link !components.StatusTag}.
 *
 * @category Component Props
 */
export interface StatusTagProps extends BaseComponentProps {
    /**
     * The color of the dot indicator.
     *
     * @defaultValue `"default"`
     */
    variant?: 'danger' | 'warning' | 'info' | 'success' | 'default';
    /**
     * When set to `true`, the status tag's dot will be a ring instead of a filled-in circle.
     *
     * @defaultValue `false`
     */
    hollow?: boolean;
    /**
     * A function that will be invoked when the status tag is clicked. It receives no arguments and its return value is ignored.
     *
     * @event
     */
    onClick?: () => void;
    /**
     * When set to `true`, the status tag will include a small, clickable x icon to remove it.
     *
     * @defaultValue `false`
     */
    showRemoveIcon?: boolean;
    /**
     * A function that will be invoked when the remove icon is clicked.
     *
     * @event
     */
    onRemoveClick?: () => void;
    /**
     * Sets the content that will render inside the StatusTag. This prop is passed implicitly by providing sub-components. We recommend only using a small amount of text.
     */
    children: ReactNode;
}
